function handle_floating_contact_btn(){const t=document.querySelector("#invitation-text"),n=document.querySelector("#floating-contact-btn"),e=t.querySelector("a");n.addEventListener("click",t=>{const e=n.style.transform;n.animate([{transform:e},{transform:"scale(0.9)"},{transform:e}],{easing:"ease-out",duration:200})}),window.addEventListener("scroll",o=>{const a=t.getBoundingClientRect(),i=a.y+a.height;e.getBoundingClientRect();i<=0?(n.classList.add("floating-contact-btn-shown"),n.classList.remove("floating-contact-btn-hidden")):(n.classList.remove("floating-contact-btn-shown"),n.classList.add("floating-contact-btn-hidden"))})}window.addEventListener("load",()=>{AOS.init(),initNavigation()}),handle_floating_contact_btn();